package Solution;

public class UnaryOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a =4;
		int b =6;
		System.out.println(a&b);
		System.out.println(a|b);
		System.out.println(~a);
		System.out.println(~b);
		System.out.println(a^b);
		System.out.println(a>>b);
	}

}
